import React from "react";
import Grid from "@material-ui/core/Grid";

import { FieldItem } from "../";

import "./Field.scss";

const FormRow = ({ fieldRow }) => {
  console.log(fieldRow);
  return (
    <React.Fragment>
      {fieldRow.map(item => (
        <Grid item>
          {!item.isEmpty ? (
            <FieldItem
              isEmpty={false}
              isMe={item.player === "1"}
              isCross={item.value === "x"}
            />
          ) : (
            <FieldItem isEmpty={true} />
          )}
        </Grid>
      ))}
    </React.Fragment>
  );
};

const Field = fieldJson => {
  const FieldJson = [
    { player: "1", value: "o" },
    { player: "0", value: "x" },
    { isEmpty: true },
    { player: "1", value: "x" },
    { isEmpty: true },
    { player: "1", value: "o" },
    { player: "0", value: "x" },
    { isEmpty: true },
    { player: "1", value: "x" }
  ];

  return (
    <div>
      <Grid container>
        <Grid container item>
          <FormRow fieldRow={fieldJson.splice(0, 3)} />
        </Grid>
        <Grid container item>
          <FormRow fieldRow={fieldJson.splice(0, 3)} />
        </Grid>
        <Grid container item>
          <FormRow fieldRow={fieldJson.splice(0, 3)} />
        </Grid>
      </Grid>
    </div>
  );
};

export default Field;
