import React from "react";
import Grid from "@material-ui/core/Grid";

import { FieldItem } from "../";

import "./Field.scss";

const FormRow = ({ fieldRow }) => {
  console.log(fieldRow);
  return (
    <React.Fragment>
      {fieldRow.map(item => (
        <Grid item>
          {!item.isEmpty ? (
            <FieldItem
              _id={item._id}
              // key={item._id}
              isEmpty={false}
              isMe={item.player === "1"}
              isCross={item.value === "x"}
            />
          ) : (
            <FieldItem key={item._id} isEmpty={true} />
          )}
        </Grid>
      ))}
    </React.Fragment>
  );
};

const Field = ({ field }) => {
  return (
    <div>
      <Grid container>
        <Grid container item>
          <FormRow fieldRow={field.splice(0, 3)} />
        </Grid>
        <Grid container item>
          <FormRow fieldRow={field.splice(0, 3)} />
        </Grid>
        <Grid container item>
          <FormRow fieldRow={field.splice(0, 3)} />
        </Grid>
      </Grid>
    </div>
  );
};

export default Field;
